import React, { useEffect, useState } from 'react';
import { Typography, Grid, Paper, Box, Button, TextField } from '@mui/material';
import { fetchUserProfile, updateProfileInfo } from '../Services/api';

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [editMode, setEditMode] = useState(false); // State for toggling edit mode

  const fetchProfileData = async () => {
    try {
      const data = await fetchUserProfile();
      setProfileData(data.UserDetails);
    } catch (error) {
      console.error('Error fetching profile data:', error);
    }
  };

  useEffect(() => {
    fetchProfileData();
  }, []);

  const handleEditProfile = () => {
    setEditMode(true);
  };

  const handleCancelEdit = () => {
    setEditMode(false);
    fetchProfileData(); // Re-fetch profile data to reset to original values
  };

  const handleUpdateProfile = async () => {
    try {
        console.log()
      const updatedData = await updateProfileInfo(profileData); // Assuming profileData contains updated values
      setProfileData(updatedData.UserDetails); // Update local state with updated data
      setEditMode(false); // Exit edit mode after successful update
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <Box mt={4} mx="auto" maxWidth={600}>
      <Paper elevation={3} style={{ padding: '16px' }}>
        <Typography variant="h5" gutterBottom>
          Profile Information
        </Typography>
        {editMode ? (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Name:
              </Typography>
              <TextField
                type="text"
                value={profileData.name}
                onChange={(e) => setProfileData({ ...profileData, name: e.target.value })}
                fullWidth
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Email:
              </Typography>
              <TextField
                type="email"
                value={profileData.email}
                onChange={(e) => setProfileData({ ...profileData, email: e.target.value })}
                fullWidth
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Birthdate:
              </Typography>
              <TextField
                type="date"
                value={profileData.birthdate.slice(0, 10)} // Use slice to get the date part only
                onChange={(e) => setProfileData({ ...profileData, birthdate: e.target.value })}
                fullWidth
                variant="outlined"
              />
            </Grid>
            {/* Add input fields for other profile information (like password) as needed */}
            <Grid item xs={12}>
              <Button onClick={handleUpdateProfile} variant="contained" color="primary">
                Confirm
              </Button>
              <Button onClick={handleCancelEdit} variant="contained" color="secondary">
                Cancel
              </Button>
            </Grid>
          </Grid>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Name:
              </Typography>
              <Typography variant="body1">{profileData.name}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Email:
              </Typography>
              <Typography variant="body1">{profileData.email}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
                Birthdate:
              </Typography>
              <Typography variant="body1">{profileData.birthdate.slice(0, 10)}</Typography>
            </Grid>
            {/* Display other profile information (like password) as needed */}
            <Grid item xs={12}>
              <Button onClick={handleEditProfile} variant="contained">
                Edit Profile
              </Button>
            </Grid>
          </Grid>
        )}
      </Paper>
    </Box>
  );
};

export default Profile;
