import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { Dialog, DialogContent, DialogActions, Button, DialogTitle } from "@mui/material";
import "./NavBar.css";
import { logOutUser } from "../Services/api"; // Import the logOutUser function

function NavBar() {
  const token = localStorage.getItem("token"); // Retrieve the token from localStorage
  const [open, setOpen] = useState(false); // State to control the dialog
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      const response = await logOutUser(); // Use the logOutUser function
      setOpen(false);
      localStorage.removeItem("token"); // Remove the token from localStorage
      navigate("/"); // Redirect to login page
    } catch (error) {
      console.error("Error logging out:", error.response?.data?.message || "An error occurred");
    }
  };

  const handleDialogOpen = () => {
    setOpen(true);
  };

  const handleDialogClose = () => {
    setOpen(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/" className="navbar-button-Header">
          Task Management System
        </Link>
      </div>
      {token === null ? (
        <div className="navbar-buttons">
          <Link to="/" className="navbar-button">
            Login
          </Link>
          <Link to="/register" className="navbar-button">
            Register
          </Link>
        </div>
      ) : (
        <div className="navbar-buttons">
          <Link onClick={handleDialogOpen} className="navbar-button">
            Logout
          </Link>
          <Link onClick={handleDialogOpen} className="navbar-button">
            Profile
          </Link>
        </div>
      )}

      {/* Logout Confirmation Dialog */}
      <Dialog open={open} onClose={handleDialogClose}>
        <DialogTitle>Confirm Logout</DialogTitle>
        <DialogContent>Are you sure you want to logout?</DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleLogout} color="primary">
            Yes, Logout
          </Button>
        </DialogActions>
      </Dialog>
    </nav>
  );
}

export default NavBar;
