import React, { useEffect, useState } from "react";
import axios from "axios";
import { Typography, Grid, Paper } from "@mui/material";

// const useStyles = makeStyles((theme) => ({
//   paper: {
//     padding: theme.spacing(2),
//     margin: "auto",
//     maxWidth: 600,
//   },
// }));

const Profile = () => {
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:9900/users/user", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        console.log(response.data)
        setProfileData(response.data); // Assuming response.data contains profile details
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };

    fetchProfileData();
  }, []);

  if (!profileData) {
    return <div>Loading...</div>; // Handle loading state if data is not yet fetched
  }

  return (
    <Paper >
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Name:</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="body1">{profileData.name}</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Email:</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="body1">{profileData.email}</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="subtitle1">Birthdate:</Typography>
        </Grid>
        <Grid item xs={6}>
          <Typography variant="body1">{profileData.birthdate}</Typography>
        </Grid>
      </Grid>
    </Paper>
  );
};

export default Profile;
