import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, DialogContent, DialogActions, Button } from '@mui/material'; // Import Material-UI components

const RegisterForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    birthdate: '',
  });
  const [open, setOpen] = useState(false); // State for dialog box

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:9900/users/signup`, formData);
      setFormData({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        birthdate: '',
      });
      setOpen(true); // Open dialog box on successful registration
    } catch (error) {
      setOpe
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Confirm Password:
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Birthdate:
          <input
            type="date"
            name="birthdate"
            value={formData.birthdate}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Register</button>
      </form>

      {/* Dialog box for showing registration success */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>
          Registration Done Successfully!
        </DialogContent>
        <DialogActions>
          <Button href='/login' color="primary">
            Go to Login
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default RegisterForm;
