import React, { useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@mui/material";
import { createUserTasks } from "../Services/api";

const AddTaskDialog = ({ open, onClose }) => {
  const [newTask, setNewTask] = useState({
    Title: "",
    Description: "",
    Due_Date: "",
    Priority: "Medium",
    Status: "Pending",
  });
  const token = localStorage.getItem("token");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({
      ...newTask,
      [name]: value,
    });
  };

  const handleSaveTask = async () => {
    try {
      if(token){
        await createUserTasks(newTask);
        onClose(true); 
      }
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  const handleClose = () => {
    onClose(false); 
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
      <DialogTitle>Add New Task</DialogTitle>
      <DialogContent dividers>
        <TextField
          name="Title"
          value={newTask.Title}
          onChange={handleInputChange}
          fullWidth
          variant="outlined"
          label="Title"
          margin="normal"
        />

        <TextField
          name="Description"
          value={newTask.Description}
          onChange={handleInputChange}
          fullWidth
          variant="outlined"
          label="Description"
          margin="normal"
        />

        <TextField
          name="Due_Date"
          value={newTask.Due_Date}
          onChange={handleInputChange}
          type="date"
          fullWidth
          variant="outlined"
          label="Due Date"
          margin="normal"
          InputLabelProps={{
            shrink: true,
          }}
        />

        <FormControl fullWidth variant="outlined" margin="normal">
          <InputLabel htmlFor="priority-select">Priority</InputLabel>
          <Select
            value={newTask.Priority}
            onChange={handleInputChange}
            label="Priority"
            inputProps={{
              name: "Priority",
              id: "priority-select",
            }}
          >
            <MenuItem value="Low">Low</MenuItem>
            <MenuItem value="Medium">Medium</MenuItem>
            <MenuItem value="High">High</MenuItem>
          </Select>
        </FormControl>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">
          Cancel
        </Button>
        <Button onClick={handleSaveTask} color="primary">
          Add Task
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default AddTaskDialog;
