import React, { useState, useEffect } from "react";
import { Dialog, DialogContent, Grid, TextField, Button, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../Services/api"; // Import the loginUser function

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [open, setOpen] = useState(false);
  const [dialogMessage, setDialogMessage] = useState("");
  const navigate = useNavigate();

  // Check if the user is already logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard");
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(formData); 
      localStorage.setItem("token", response.token); 
      setFormData({
        email: "",
        password: "",
      });
      setDialogMessage("Logged in Successfully!");
      setOpen(true);
      navigate("/dashboard"); 
    } catch (error) {
      console.log(error.response.data.error);
      const errorMessage = error.response?.data?.message || "An error occurred";
      setDialogMessage(errorMessage);
      setOpen(true);
    }
  };

  return (
    <Grid container justifyContent="center" alignItems="center" minHeight="80vh">
      <Grid item xs={12} sm={8} md={6} lg={4}>
        <Box p={3} boxShadow={3} borderRadius={2}>
          <Typography variant="h4" component="h1" align="center" gutterBottom>
            Login
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Email"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              fullWidth
              margin="normal"
            />
            <TextField
              label="Password"
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              fullWidth
              margin="normal"
            />
            <Box mt={2}>
              <Button type="submit" variant="contained" color="primary" fullWidth>
                Login
              </Button>
            </Box>
          </form>
        </Box>
      </Grid>

      {/* Dialog box for showing login success or error */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>{dialogMessage}</DialogContent>
      </Dialog>
    </Grid>
  );
};

export default LoginForm;
