import React, { useState, useEffect } from "react";
import { Dialog, DialogContent, Grid, TextField, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../Services/api"; // Import the loginUser function

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [open, setOpen] = useState(false);
  const [dialogMessage, setDialogMessage] = useState("");
  const navigate = useNavigate();

  // Check if the user is already logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard");
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(formData); // Use the loginUser function
      localStorage.setItem("token", response.token); // Set the token in localStorage before navigating
      setFormData({
        email: "",
        password: "",
      });
      setDialogMessage("Logged in Successfully!");
      setOpen(true);
      navigate("/dashboard"); // Navigate after setting the token
    } catch (error) {
      const errorMessage = error.response?.data?.message || "An error occurred";
      setDialogMessage(errorMessage);
      setOpen(true);
    }
  };

  return (
    <Grid container spacing={2} justifyContent="center" style={{ marginTop: "20px" }} >
      <Grid item xs={12} md={6}>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2} direction="column">
            <Grid item xs={12}>
              <TextField
                label="Email"
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                fullWidth
                margin="normal"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Password"
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
                fullWidth
                margin="normal"
              />
            </Grid>
            <Grid item xs={12}>
              <Button type="submit" variant="contained" color="primary" fullWidth>
                Login
              </Button>
            </Grid>
          </Grid>
        </form>
      </Grid>

      {/* Dialog box for showing login success or error */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>{dialogMessage}</DialogContent>
      </Dialog>
    </Grid>
  );
};

export default LoginForm;
