import React, { useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  TextField,
} from "@mui/material";
import { updateUserTask } from "../Services/api";

const TaskDetailsDialog = ({ taskDetails, open, onClose }) => {
  const [updatedTaskDetails, setUpdatedTaskDetails] = useState({
    Title: taskDetails.Title,
    Description: taskDetails.Description,
    Due_Date: taskDetails.Due_Date ? new Date(taskDetails.Due_Date).toISOString().split('T')[0] : '',
    Priority: taskDetails.Priority,
    Status: taskDetails.Status,
  });

  const handleClose = () => {
    onClose();
  };

  const handleModify = () => {
    setOpenModifyDialog(true);
  };

  const handleSaveChanges = async () => {
    try {
      await updateUserTask(taskDetails._id, updatedTaskDetails);
      console.log("Task updated successfully!");
      onClose(); // Close the dialog after successful update
    } catch (error) {
      console.error("Error updating task:", error);
      // Handle error scenario here
    }
  };

  const handleCancelChanges = () => {
    // Reset the updated task details to current task details
    setUpdatedTaskDetails({
      Title: taskDetails.Title,
      Description: taskDetails.Description,
      Due_Date: taskDetails.Due_Date ? new Date(taskDetails.Due_Date).toISOString().split('T')[0] : '',
      Priority: taskDetails.Priority,
      Status: taskDetails.Status,
    });
    setOpenModifyDialog(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedTaskDetails({
      ...updatedTaskDetails,
      [name]: value,
    });
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
      <DialogTitle>Task Details</DialogTitle>
      <DialogContent dividers>
        <Typography variant="subtitle1">Title:</Typography>
        <Typography variant="body1">{taskDetails.Title}</Typography>

        <Typography variant="subtitle1">Description:</Typography>
        <Typography variant="body1">{taskDetails.Description}</Typography>

        <Typography variant="subtitle1">Due Date:</Typography>
        <Typography variant="body1">
          {taskDetails.Due_Date ? new Date(taskDetails.Due_Date).toLocaleDateString() : ''}
        </Typography>

        <Typography variant="subtitle1">Priority:</Typography>
        <Typography variant="body1">{taskDetails.Priority}</Typography>

        <Typography variant="subtitle1">Status:</Typography>
        <Typography variant="body1">{taskDetails.Status}</Typography>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">
          Close
        </Button>
        <Button onClick={handleModify} color="primary">
          Modify
        </Button>
      </DialogActions>

      {/* Modify Dialog */}
      <Dialog
        open={openModifyDialog}
        onClose={handleCloseModifyDialog}
        fullWidth
        maxWidth="sm"
      >
        <DialogTitle>Modify Task</DialogTitle>
        <DialogContent dividers>
          <Typography variant="subtitle1">Modify Task Details:</Typography>
          <TextField
            name="Title"
            value={updatedTaskDetails.Title}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Title"
            margin="normal"
          />

          <TextField
            name="Description"
            value={updatedTaskDetails.Description}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Description"
            margin="normal"
          />

          <TextField
            name="Due_Date"
            value={updatedTaskDetails.Due_Date}
            onChange={handleInputChange}
            type="date"
            fullWidth
            variant="outlined"
            label="Due Date"
            margin="normal"
          />

          <TextField
            name="Priority"
            value={updatedTaskDetails.Priority}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Priority"
            select
            margin="normal"
          >
            <MenuItem value="Low">Low</MenuItem>
            <MenuItem value="Medium">Medium</MenuItem>
            <MenuItem value="High">High</MenuItem>
          </TextField>

          <TextField
            name="Status"
            value={updatedTaskDetails.Status}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Status"
            select
            margin="normal"
          >
            <MenuItem value="Pending">Pending</MenuItem>
            <MenuItem value="In-Progress">In-Progress</MenuItem>
            <MenuItem value="Completed">Completed</MenuItem>
          </TextField>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCancelChanges} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSaveChanges} color="primary">
            Save Changes
          </Button>
        </DialogActions>
      </Dialog>
    </Dialog>
  );
};

export default TaskDetailsDialog;
