import React, { useState } from "react";
import axios from "axios";
import { Dialog, DialogContent, DialogActions, Button } from "@mui/material";

const RegisterForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [open, setOpen] = useState(false);
  const [dialogMessage, setDialogMessage] = useState(""); // State for dialog message

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:9900/users/login`,
        formData
      );
      setFormData({
        email: "",
        password: "",
      });
      setDialogMessage("Logged in Successfully!"); // Success message
      setOpen(true);
      href
    } catch (error) {
      const errorMessage = error.response?.data?.message || "An error occurred";
      console.log(error.response.data.message);
      setDialogMessage(errorMessage); // Set error message from response
      setOpen(true);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
        <br />
          Password:  
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <br />
        <button type="submit">Register</button>
      </form>

      {/* Dialog box for showing registration success or error */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>{dialogMessage}</DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default RegisterForm;
