import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
} from "@mui/material";
import { fetchUserTasks } from "../Services/api"; // Adjust this import based on your API service function
import TaskDetailsDialog from "./"; // Import the TaskDetailsDialog component

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null); // State to hold the selected task details

  useEffect(() => {
    const fetchTaskList = async () => {
      try {
        const data = await fetchUserTasks();
        setTasks(data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      }
    };

    fetchTaskList();
  }, []);

  const handleOpenDialog = (task) => {
    setSelectedTask(task);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const priorityStyles = {
    High: { color: 'red' },
    Medium: { color: 'orange' },
    Low: { color: 'green' },
  };

  const statusStyles = {
    Completed: { color: 'green' },
    'In Progress': { color: 'blue' },
    Pending: { color: 'grey' },
  };

  return (
    <div>
      <List>
        {tasks.map((task) => (
          <ListItem
            key={task._id}
            button
            onClick={() => handleOpenDialog(task)} // Open dialog when a task is clicked
            component={Link}
            style={{ textDecoration: "none", color: "inherit" }}
          >
            <ListItemText
              primary={task.Title}
              secondary={
                <>
                  <Typography
                    component="span"
                    variant="body2"
                    sx={statusStyles[task.Status]}
                  >
                    Status: {task.Status}
                  </Typography>
                  <br />
                  <Typography
                    component="span"
                    variant="body2"
                    sx={priorityStyles[task.Priority]}
                  >
                    Priority: {task.Priority}
                  </Typography>
                  <br />
                  Created At: {new Date(task.createdAt).toLocaleString()}
                </>
              }
            />
            {/* Add an icon button or any action you want to perform */}
            <ListItemSecondaryAction>
              <IconButton edge="end" aria-label="view-task-details">
                {/* Add an icon here if needed */}
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>

      {/* Task Details Dialog */}
      {selectedTask && (
        <TaskDetailsDialog
          taskDetails={selectedTask}
          open={openDialog}
          onClose={handleCloseDialog}
          onModify={() => {
            // Handle modify task action
            // For example, navigate to modify task page
            console.log("Modify task clicked");
            setOpenDialog(false); // Close dialog after modify action
          }}
        />
      )}
    </div>
  );
};

export default TaskList;
