import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, Paper, Box, Button, Grid } from '@mui/material';
import { fetchTaskDetails } from '../Services/api'; // Adjust this import based on your API service function

const TaskDetails = () => {
  const { id } = useParams(); // Get the task ID from URL params
  const [taskDetails, setTaskDetails] = useState(null);

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const data = await fetchTaskDetails(id); // Replace with your fetch task details function
        setTaskDetails(data.task); // Assuming data.task contains the task details object
      } catch (error) {
        console.error('Error fetching task details:', error);
      }
    };

    fetchTask();
  }, [id]);

  if (!taskDetails) {
    return <div>Loading...</div>;
  }

  return (
    <Box mt={4} mx="auto" maxWidth={600}>
      <Paper elevation={3} style={{ padding: '16px' }}>
        <Typography variant="h5" gutterBottom>
          Task Details
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Title:
            </Typography>
            <Typography variant="body1">{taskDetails.Title}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Description:
            </Typography>
            <Typography variant="body1">{taskDetails.Description}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Due Date:
            </Typography>
            <Typography variant="body1">
              {new Date(taskDetails.Due_Date).toLocaleDateString()}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Priority:
            </Typography>
            <Typography variant="body1">{taskDetails.Priority}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Status:
            </Typography>
            <Typography variant="body1">{taskDetails.Status}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: 'bold' }}>
              Created At:
            </Typography>
            <Typography variant="body1">
              {new Date(taskDetails.createdAt).toLocaleString()}
            </Typography>
          </Grid>
        </Grid>
        {/* Add any additional details or actions as needed */}
        <Button variant="contained" color="primary" onClick={() => alert('Edit functionality')}>
          Edit Task
        </Button>
      </Paper>
    </Box>
  );
};

export default TaskDetails;
