import React, { useEffect, useState } from "react";
import { Typography, Grid, Paper, Box } from "@mui/material";
import { fetchUserProfile } from "../Services/api";

const Profile = () => {
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const data = await fetchUserProfile();
        setProfileData(data.UserDetails);
      } catch (error) {
        console.error("Error fetching profile data:", error);
      }
    };

    fetchProfileData();
  }, []);

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <Box mt={4} mx="auto" maxWidth={600}>
      <Paper elevation={3}>
        <Typography variant="h5" gutterBottom style={{ padding: "16px" }}>
          Profile Information
        </Typography>
        <Grid container spacing={3} style={{ padding: "16px" }}>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
              Name:
            </Typography>
            <Typography variant="body1">{profileData.name}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
              Email:
            </Typography>
            <Typography variant="body1">{profileData.email}</Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
              Birthdate:
            </Typography>
            <Typography variant="body1">
              {new Date(profileData.birthdate).toLocaleDateString()}
            </Typography>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};

export default Profile;
