import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, DialogContent, DialogActions, Button } from '@mui/material'; // Import Material-UI components

const LoginForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [open, setOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:9900/users/login`, formData);
      setFormData({
        email: '',
        password: '',
      });
      setOpen(true); // Open dialog box on successful registration
    } catch (error) {
      console.error('Registration failed:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <button type="submit">Register</button>
      </form>

      {/* Dialog box for showing registration success */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>
          Logged in Successfully!
        </DialogContent>
        <DialogActions>
          <Button href='/' color="primary">Dashboard
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default LoginForm;
