import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Typography, List, ListItem, ListItemText, ListItemSecondaryAction, IconButton } from '@mui/material';
import { fetchUserTasks } from '../Services/api'; // Adjust this import based on your API service function

const TaskList = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTaskList = async () => {
      try {
        const data = await fetchUserTasks(); // Replace with your fetch tasks function
        setTasks(tasks); // Assuming data.tasks contains the array of tasks
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTaskList();
  }, []);

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Task List
      </Typography>
      <List>
        {tasks.map((task) => (
          <ListItem
            key={task._id}
            button
            component={Link}
            to={`/task/${task._id}`}
            style={{ textDecoration: 'none', color: 'inherit' }}
          >
            <ListItemText
              primary={task.Title}
              secondary={`Created At: ${new Date(task.createdAt).toLocaleString()}`}
            />
            {/* Add an icon button or any action you want to perform */}
            <ListItemSecondaryAction>
              <IconButton edge="end" aria-label="view-task-details">
                {/* Add an icon here if needed */}
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default TaskList;
