import React, { useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@mui/material";
import { updateUserTask } from "../Services/api";
import AddTaskDialog from "./"; // Import the AddTaskDialog component
import "./taskDetails.css"; // Import the CSS file

const TaskDetailsDialog = ({ taskDetails, open, onClose, onModify }) => {
  const [openModifyDialog, setOpenModifyDialog] = useState(false); // State for modify dialog
  const [openAddDialog, setOpenAddDialog] = useState(false); // State for add task dialog
  const [updatedTaskDetails, setUpdatedTaskDetails] = useState({
    Title: taskDetails.Title,
    Description: taskDetails.Description,
    Due_Date: taskDetails.Due_Date
      ? new Date(taskDetails.Due_Date).toISOString().split("T")[0]
      : "", // Set initial Due Date if present
    Priority: taskDetails.Priority,
    Status: taskDetails.Status,
  });

  const handleClose = () => {
    onClose();
  };

  const handleModify = () => {
    setOpenModifyDialog(true); // Open modify dialog when Modify button is clicked
  };

  const handleSaveChanges = async () => {
    try {
      if (!taskDetails._id || typeof taskDetails._id !== 'string' || taskDetails._id.length !== 24) {
        throw new Error('Invalid task ID');
      }
      await updateUserTask(updatedTaskDetails , taskDetails._id);
      onClose();
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const handleCancelChanges = () => {
    // Reset the updated task details to current task details
    setUpdatedTaskDetails({
      Title: taskDetails.Title,
      Description: taskDetails.Description,
      Due_Date: taskDetails.Due_Date
        ? new Date(taskDetails.Due_Date).toISOString().split("T")[0]
        : "", // Reset Due Date if present
      Priority: taskDetails.Priority,
      Status: taskDetails.Status,
    });
    setOpenModifyDialog(false); // Close modify dialog
  };

  const handleAddTask = () => {
    setOpenAddDialog(true); // Open add task dialog when Add Task button is clicked
  };

  const handleCloseAddDialog = (success) => {
    setOpenAddDialog(false); // Close add task dialog
    if (success) {
      // If task was successfully added, refresh or update tasks list
      // You can implement a function here to fetch updated tasks
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedTaskDetails({
      ...updatedTaskDetails,
      [name]: value,
    });
  };

  const handleCloseModifyDialog = () => {
    setOpenModifyDialog(false);
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  };

  return (
    <>
      <Dialog open={open} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle className="dialog-title">{taskDetails.Title}</DialogTitle>
        <DialogContent dividers className="dialog-content">

          <Typography variant="subtitle1">Description:</Typography>
          <Typography variant="body1">{taskDetails.Description}</Typography>

          <Typography variant="subtitle1">Due Date:</Typography>
          <Typography variant="body1">
            {formatDate(taskDetails.Due_Date)}
          </Typography>

          <Typography variant="subtitle1">Priority:</Typography>
          <Typography variant="body1">{taskDetails.Priority}</Typography>

          <Typography variant="subtitle1">Status:</Typography>
          <Typography variant="body1">{taskDetails.Status}</Typography>

          {/* Conditional rendering of Completion Date and Time Spent */}
          {taskDetails.Status === "Completed" && (
            <>
              <Typography variant="subtitle1">Completion Date:</Typography>
              <Typography variant="body1">
                {formatDate(taskDetails.Completion_Date)}
              </Typography>

              <Typography variant="subtitle1">Time Spent:</Typography>
              <Typography variant="body1">{taskDetails.TimeSpent}</Typography>
            </>
          )}
        </DialogContent>
        <DialogActions className="dialog-actions">
          <Button onClick={handleClose} color="secondary">
            Close
          </Button>
          <Button onClick={handleModify} color="primary">
            Modify
          </Button>
          <Button onClick={handleAddTask} color="primary">
            Add Task
          </Button>
        </DialogActions>
      </Dialog>

      {/* Modify Dialog */}
      <Dialog
        open={openModifyDialog}
        onClose={handleCloseModifyDialog}
        fullWidth
        maxWidth="sm"
      >
        <DialogTitle className="dialog-title">Modify Task</DialogTitle>
        <DialogContent dividers className="dialog-content">
          <Typography variant="subtitle1">Modify Task Details:</Typography>
          <TextField
            name="Title"
            value={updatedTaskDetails.Title}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Title"
            margin="normal"
          />

          <TextField
            name="Description"
            value={updatedTaskDetails.Description}
            onChange={handleInputChange}
            fullWidth
            variant="outlined"
            label="Description"
            margin="normal"
          />

          <TextField
            name="Due_Date"
            value={updatedTaskDetails.Due_Date}
            onChange={handleInputChange}
            type="date"
            fullWidth
            variant="outlined"
            label="Due Date"
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <FormControl fullWidth variant="outlined" margin="normal">
            <InputLabel htmlFor="priority-select-modify">Priority</InputLabel>
            <Select
              value={updatedTaskDetails.Priority}
              onChange={handleInputChange}
              label="Priority"
              inputProps={{
                name: "Priority",
                id: "priority-select-modify",
              }}
            >
              <MenuItem value="Low">Low</MenuItem>
              <MenuItem value="Medium">Medium</MenuItem>
              <MenuItem value="High">High</MenuItem>
            </Select>
          </FormControl>

          <FormControl fullWidth variant="outlined" margin="normal">
            <InputLabel htmlFor="status-select-modify">Status</InputLabel>
            <Select
              value={updatedTaskDetails.Status}
              onChange={handleInputChange}
              label="Status"
              inputProps={{
                name: "Status",
                id: "status-select-modify",
              }}
            >
              <MenuItem value="Pending">Pending</MenuItem>
              <MenuItem value="In-Progress">In-Progress</MenuItem>
              <MenuItem value="Completed">Completed</MenuItem>
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions className="dialog-actions">
          <Button onClick={handleCancelChanges} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSaveChanges} color="primary">
            Save Changes
          </Button>
        </DialogActions>
      </Dialog>

      {/* Add Task Dialog */}
      <AddTaskDialog open={openAddDialog} onClose={handleCloseAddDialog} />
    </>
  );
};

export default TaskDetailsDialog;
