import React, { useEffect, useState } from "react";
import { Typography, Grid, Paper, Box, Button } from "@mui/material";
<DatePicker label="Basic date picker" />
import { fetchUserProfile, updateProfileInfo } from "../Services/api";

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [editMode, setEditMode] = useState(false); // State for toggling edit mode

  const fetchProfileData = async () => {
    try {
      const data = await fetchUserProfile();
      setProfileData(data.UserDetails);
    } catch (error) {
      console.error("Error fetching profile data:", error);
    }
  };

  useEffect(() => {
    fetchProfileData();
  }, []);

  const formatBirthdate = (birthdate) => {
    if (!birthdate) return "";
    const date = new Date(birthdate);
    return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
  };

  const handleEditProfile = () => {
    setEditMode(true);
  };

  const handleCancelEdit = () => {
    setEditMode(false);
    fetchProfileData(); // Re-fetch profile data to reset to original values
  };

  const handleUpdateProfile = async () => {
    try {
      // Implement your update logic here
      const updatedData = await updateProfileInfo(profileData); // Assuming profileData contains updated values
      setProfileData(updatedData.UserDetails); // Update local state with updated data
      setEditMode(false); // Exit edit mode after successful update
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  return (
    <Box mt={4} mx="auto" maxWidth={600}>
      <Paper elevation={3} style={{ padding: "16px" }}>
        <Typography variant="h5" gutterBottom>
          Profile Information
        </Typography>
        {editMode ? (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Name:
              </Typography>
              <input
                type="text"
                value={profileData.name}
                onChange={(e) =>
                  setProfileData({ ...profileData, name: e.target.value })
                }
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Email:
              </Typography>
              <input
                type="email"
                value={profileData.email}
                onChange={(e) =>
                  setProfileData({ ...profileData, email: e.target.value })
                }
              />
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Birthdate:
              </Typography>
              <input
                type="date"
                value={profileData.birthdate}
                onChange={(e) =>
                  setProfileData({ ...profileData, birthdate: e.target.value })
                }
              />
            </Grid>
            {/* Add input fields for other profile information (like password) as needed */}
            <Grid item xs={12}>
              <Button
                onClick={handleUpdateProfile}
                variant="contained"
                color="primary"
              >
                Confirm
              </Button>
              <Button
                onClick={handleCancelEdit}
                variant="contained"
                color="secondary"
              >
                Cancel
              </Button>
            </Grid>
          </Grid>
        ) : (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Name:
              </Typography>
              <Typography variant="body1">{profileData.name}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Email:
              </Typography>
              <Typography variant="body1">{profileData.email}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="subtitle1" style={{ fontWeight: "bold" }}>
                Birthdate:
              </Typography>
              <Typography variant="body1">
                {formatBirthdate(profileData.birthdate)}
              </Typography>
            </Grid>
            {/* Display other profile information (like password) as needed */}
            <Grid item xs={12}>
              <Button onClick={handleEditProfile} variant="contained">
                Edit Profile
              </Button>
            </Grid>
          </Grid>
        )}
      </Paper>
    </Box>
  );
};

export default Profile;
