import React, { useState } from "react";
import { Dialog, DialogContent } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../Services/api"; // Import the loginUser function

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [open, setOpen] = useState(false);
  const [dialogMessage, setDialogMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(formData); // Use the loginUser function
      setFormData({
        email: "",
        password: "",
      });
      setDialogMessage("Logged in Successfully!");
      localStorage.setItem("token", response.token); // Set the token in localStorage
      setOpen(true);
      setTimeout(() => {
        setOpen(false);
        navigate("/dashboard"); // Redirect to dashboard page
      }, 2000); // Close dialog and navigate after 3 seconds
    } catch (error) {
      const errorMessage = error.response?.data?.message || "An error occurred";
      setDialogMessage(errorMessage);
      setOpen(true);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>

      {/* Dialog box for showing login success or error */}
      <Dialog open={open} onClose={handleClose}>
        <DialogContent>{dialogMessage}</DialogContent>
      </Dialog>
    </div>
  );
};

export default LoginForm;
